mapper.select:
select attachment_no
from category_image
where category_no = #{value}

mapper.insert:
insert into category_image (
    category_no,
    attachment_no
) values (
    #{categoryNo},
    #{attachmentNo}
)

insert into category_mapping (
    restaurant_id,
    category_no
) values (
    #{restaurantId},
    #{categoryNo}
)
