mapper.insert:
insert into category(
    category_no, category_name, parent_category, category_order
) values (
    #{categoryNo}, #{categoryName}, #{parentCategory}, #{categoryOrder}
)

mapper.update:
update category
set
    category_name = #{categoryName},
    parent_category = #{parentCategory},
    category_order = #{categoryOrder}
where category_no = #{categoryNo}
