mapper.insert:
insert into member_token(
	member_token_no, member_token_target, member_token_value
)
values( 
	member_token_seq.nextval, 
	#{memberTokenTarget}, 
	#{memberTokenValue}
)

mapper.select:
select * from member_token 
where 
	member_token_target = #{memberTokenTarget} 
	and 
	member_token_value = #{memberTokenValue}
