mapper.select:
select * from (
	select rownum rn, TMP.* from (
		select * from restaurant
		where restaurant_status = 'N' 
		order by restaurant_id asc
	)TMP 
) where rn between #{begin} and #{end}

select * from restaurant 
where restaurant_id = #{restaurantId}
and restaurant_status = 'N'

select * from restaurant 
where owner_id = #{ownerId} 
and restaurant_status = 'N'

select * from restaurant_list
where restaurant_id = #{restaurantId} 
and owner_id = #{ownerId} 
and restaurant_status = 'N'

mapper.update:
update restaurant set 
restaurant_place = #{restaurantPlace},
restaurant_address = #{restaurantAddress},
restaurant_address_x = #{restaurantAddressX},
restaurant_address_y = #{restaurantAddressY}
where restaurant_id = #{restaurantId}

update restaurant set 
restaurant_place = #{restaurantPlace},
restaurant_address = #{restaurantAddress},
restaurant_address_x = #{restaurantAddressX},
restaurant_address_y = #{restaurantAddressY}
where restaurant_id = #{restaurantId}

update restaurant
owner_id = #{ownerId}, restaurant_name = #{restaurantName}, restaurant_open = #{restaurantOpen}, restaurant_close = #{restaurantClose}, restaurant_break_start = #{restaurantBreakStart}, restaurant_break_end = #{restaurantBreakEnd}, reservation_interval = #{reservationInterval}, restaurant_contact = #{restaurantContact}, restaurant_description = #{restaurantDescription}, restaurant_last_order = #{restaurantLastOrder}, restaurant_opening_days = #{restaurantOpeningDays}, restaurant_status = #{restaurantStatus},
where restaurant_id = #{restaurantId}
