mapper.insert:
insert into seat(
seat_id, seat_restaurant_id, seat_type, 
seat_max_people
) 
values(
#{seatId}, #{seatRestaurantId}, #{seatType}, 
#{seatMaxPeople}
)

mapper.select:
select * from seat
where seat_restaurant_id = #{seatRestaurantId} 
order by seat_id desc

select seat_type, seat_max_people , count(*) as count from seat 
where seat_restaurant_id = #{seatRestaurantId} 
group by seat_type, seat_max_people order by seat_type, seat_max_people
